// --- Helper Functions ---
const Icon = ({ path, className = "w-6 h-6" }) => (
    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor" className={className}>
        <path d={path} />
    </svg>
);

const getFileInfo = (item) => {
    const { kind, mimeType } = item;
    if (kind === 'folder') return { type: 'Folder', path: "M10 4H4c-1.1 0-1.99.9-1.99 2L2 18c0 1.1.9 2 2 2h16c1.1 0 2-.9 2-2V8c0-1.1-.9-2-2-2h-8l-2-2z", color: "text-yellow-500" };
    if (mimeType) {
        if (mimeType.includes('pdf')) return { type: 'PDF', path: "M20 2H8c-1.1 0-2 .9-2 2v12c0 1.1.9 2 2 2h12c1.1 0 2-.9 2-2V4c0-1.1-.9-2-2-2zm-8.5 7.5c0 .83-.67 1.5-1.5 1.5H9v2H7.5V7H10c.83 0 1.5.67 1.5 1.5v1zM18 14H14v-4h4v4z", color: "text-red-500" };
        if (mimeType.includes('word')) return { type: 'Word', path: "M14 2H6c-1.1 0-1.99.9-1.99 2L4 20c0 1.1.89 2 1.99 2H18c1.1 0 2-.9 2-2V8l-6-6zm2 16H8v-2h8v2zm0-4H8v-2h8v2zm-3-5V3.5L18.5 9H13z", color: "text-blue-500" };
        if (mimeType.includes('sheet') || mimeType.includes('excel')) return { type: 'Excel', path: "M14 2H6c-1.1 0-2 .9-2 2v12c0 1.1.9 2 2 2h12c1.1 0 2-.9 2-2V8l-6-6zm2 16H8v-2h8v2zm0-4H8v-2h8v2zm-3-5V3.5L18.5 9H13z", color: "text-emerald-500" };
        if (mimeType.includes('plain')) return { type: 'Text', path: "M14 2H6c-1.1 0-2 .9-2 2v16c0 1.1.9 2 2 2h12c1.1 0 2-.9 2-2V8l-6-6zM15 16H9v-2h6v2zm0-4H9v-2h6v2z", color: "text-gray-500" };
    }
    return { type: 'File', path: "M6 2c-1.1 0-2 .9-2 2v16c0 1.1.9 2 2 2h12c1.1 0 2-.9 2-2V4c0-1.1-.9-2-2-2H6zm0 18V4h12v16H6z", color: "text-gray-500" };
};

// --- Modal Components ---
const Modal = ({ isOpen, onClose, title, children }) => {
    if (!isOpen) return null;
    return (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50" onClick={onClose}>
            <div className="bg-white rounded-lg p-6 w-full max-w-sm" onClick={e => e.stopPropagation()}>
                <h2 className="text-xl font-bold mb-4">{title}</h2>
                {children}
            </div>
        </div>
    );
};

const NewFolderModal = ({ isOpen, onClose, onSubmit, t }) => {
    const [name, setName] = useState('');
    const handleSubmit = (e) => { e.preventDefault(); if (name.trim()) { onSubmit(name.trim()); setName(''); onClose(); } };
    return (
        <Modal isOpen={isOpen} onClose={onClose} title={t.manageFilesPage.createFolder}>
            <form onSubmit={handleSubmit}>
                <input type="text" value={name} onChange={(e) => setName(e.target.value)} placeholder={t.manageFilesPage.folderName} className="w-full py-2 px-3 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none" autoFocus />
                <div className="flex justify-end gap-3 mt-5">
                    <button type="button" onClick={onClose} className="bg-gray-200 text-gray-800 font-semibold py-2 px-5 rounded-lg hover:bg-gray-300">{t.manageFilesPage.cancel}</button>
                    <button type="submit" className="bg-blue-600 text-white font-semibold py-2 px-5 rounded-lg hover:bg-blue-700">{t.manageFilesPage.create}</button>
                </div>
            </form>
        </Modal>
    );
};

const RenameModal = ({ isOpen, onClose, onSubmit, t, currentName }) => {
    const [name, setName] = useState(currentName);
    useEffect(() => { setName(currentName) }, [currentName]);
    const handleSubmit = (e) => { e.preventDefault(); if (name.trim()) { onSubmit(name.trim()); onClose(); } };
    return (
        <Modal isOpen={isOpen} onClose={onClose} title={t.renameItem}>
            <form onSubmit={handleSubmit}>
                <input type="text" value={name} onChange={(e) => setName(e.target.value)} className="w-full py-2 px-3 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none" autoFocus />
                <div className="flex justify-end gap-3 mt-5">
                    <button type="button" onClick={onClose} className="bg-gray-200 text-gray-800 font-semibold py-2 px-5 rounded-lg hover:bg-gray-300">{t.manageFilesPage.cancel}</button>
                    <button type="submit" className="bg-blue-600 text-white font-semibold py-2 px-5 rounded-lg hover:bg-blue-700">{t.rename}</button>
                </div>
            </form>
        </Modal>
    );
};

const ConfirmDeleteModal = ({ isOpen, onClose, onConfirm, t, item }) => {
    return (
        <Modal isOpen={isOpen} onClose={onClose} title={t.confirmDeletion}>
            <p>{t.deleteWarning}</p>
            {item?.kind === 'folder' && <p className="mt-2 font-semibold text-red-600">{t.deleteFolderWarning}</p>}
            <div className="flex justify-end gap-3 mt-5">
                <button type="button" onClick={onClose} className="bg-gray-200 text-gray-800 font-semibold py-2 px-5 rounded-lg hover:bg-gray-300">{t.manageFilesPage.cancel}</button>
                <button type="button" onClick={onConfirm} className="bg-red-600 text-white font-semibold py-2 px-5 rounded-lg hover:bg-red-700">{t.delete}</button>
            </div>
        </Modal>
    );
};

const FilePreviewModal = ({ isOpen, onClose, t, item }) => {
    if (!item) return null;
    return (
        <Modal isOpen={isOpen} onClose={onClose} title={t.filePreview}>
            <div className="flex flex-col items-center">
                <Icon path={item.icon.path} className={`w-32 h-32 ${item.icon.color}`} />
                <p className="text-lg font-bold mt-4 break-all">{item.name}</p>
                <p className="text-gray-600">{item.icon.type}</p>
                <p className="text-gray-500 text-sm mt-2">{item.size} - {item.modified}</p>
                <button type="button" onClick={onClose} className="mt-6 bg-blue-600 text-white font-semibold py-2 px-8 rounded-lg hover:bg-blue-700">{t.close}</button>
            </div>
        </Modal>
    );
};

// --- Context Menu ---
const ContextMenu = ({ x, y, isOpen, onClose, onRename, onDelete, t }) => {
    const ref = useRef(null);
    useEffect(() => {
        const handleClickOutside = (event) => { if (ref.current && !ref.current.contains(event.target)) { onClose(); } };
        document.addEventListener("mousedown", handleClickOutside);
        return () => document.removeEventListener("mousedown", handleClickOutside);
    }, [onClose]);
    if (!isOpen) return null;
    return (
        <div ref={ref} style={{ top: y, left: x }} className="absolute bg-white shadow-lg rounded-md py-2 z-50 w-40">
            <button onClick={onRename} className="w-full text-left px-4 py-2 hover:bg-gray-100 flex items-center gap-3"><Icon path="M3 17.25V21h3.75L17.81 9.94l-3.75-3.75L3 17.25zM20.71 7.04c.39-.39.39-1.02 0-1.41l-2.34-2.34a.9959.9959 0 00-1.41 0l-1.83 1.83 3.75 3.75 1.83-1.83z" className="w-5 h-5" /> {t.rename}</button>
            <button onClick={onDelete} className="w-full text-left px-4 py-2 hover:bg-gray-100 flex items-center gap-3 text-red-600"><Icon path="M6 19c0 1.1.9 2 2 2h8c1.1 0 2-.9 2-2V7H6v12zM19 4h-3.5l-1-1h-5l-1 1H5v2h14V4z" className="w-5 h-5" /> {t.delete}</button>
        </div>
    );
};

// --- Toast Notification ---
const Toast = ({ message, show, onHide }) => {
    useEffect(() => {
        if (show) {
            const timer = setTimeout(onHide, 3000);
            return () => clearTimeout(timer);
        }
    }, [show, onHide]);

    return (
        <div className={`fixed bottom-8 left-1/2 -translate-x-1/2 bg-gray-800 text-white px-6 py-3 rounded-lg shadow-lg transition-all duration-300 ${show ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4'}`}>
            {message}
        </div>
    );
};

// --- Pages/Views ---
const DashboardPage = ({ t, onFileUpload, files, openModal, showToast, onDeleteFile }) => {
    const [isDragging, setIsDragging] = useState(false);
    const [uploadError, setUploadError] = useState('');

    const handleFiles = (fileList) => {
        const validFiles = Array.from(fileList).filter(file => FILE_ALLOWED_TYPES.includes(file.type));
        const invalidFilesCount = fileList.length - validFiles.length;

        if (invalidFilesCount > 0) {
            setUploadError(t.uploadError);
            setTimeout(() => setUploadError(''), 5000);
        } else {
            setUploadError('');
        }

        if (validFiles.length > 0) {
            onFileUpload(validFiles, 'root');
        }
    };

    const handleDragEvents = (e, dragging) => {
        e.preventDefault();
        e.stopPropagation();
        setIsDragging(dragging);
    };

    const handleDrop = useCallback((e) => {
        handleDragEvents(e, false);
        handleFiles(e.dataTransfer.files);
    }, [t, onFileUpload]);

    const handleFileSelect = (e) => {
        handleFiles(e.target.files);
    };

    const recentFiles = files.filter(f => f.kind === 'file').slice(0, 4);

    return (
        <>
            <div 
                className={`bg-white p-6 rounded-lg shadow-md transition-all duration-300 ${isDragging ? 'border-blue-500 ring-2 ring-blue-300' : 'border-gray-300'}`}
                onDragEnter={(e) => handleDragEvents(e, true)}
                onDragLeave={(e) => handleDragEvents(e, false)}
                onDragOver={(e) => e.preventDefault()}
                onDrop={handleDrop}
            >
                <h2 className="text-xl font-bold text-gray-800 mb-4 pb-3 border-b">{t.uploadNewFile}</h2>
                <div className="border-2 border-dashed border-gray-300 rounded-lg p-8 text-center bg-gray-50">
                    <Icon path="M19.35 10.04C18.67 6.59 15.64 4 12 4 9.11 4 6.6 5.64 5.35 8.04 2.34 8.36 0 10.91 0 14c0 3.31 2.69 6 6 6h13c2.76 0 5-2.24 5-5 0-2.64-2.05-4.78-4.65-4.96zM14 13v4h-4v-4H7l5-5 5 5h-3z" className="w-16 h-16 mx-auto text-blue-500" />
                    <p className="mt-4 text-gray-600">{t.dragDrop} <span className="font-bold text-blue-600">{t.browseFiles}</span></p>
                    <input type="file" id="fileInput" multiple className="hidden" onChange={handleFileSelect} accept={FILE_ALLOWED_TYPES.join(',')} />
                    <button onClick={() => document.getElementById('fileInput').click()} className="mt-6 bg-blue-600 text-white font-semibold py-2 px-6 rounded-lg hover:bg-blue-700 transition-colors duration-300 shadow-sm">
                        {t.selectFiles}
                    </button>
                </div>
                {uploadError && (
                    <div className="mt-4 text-center text-red-600 bg-red-100 p-3 rounded-lg font-semibold">
                        {uploadError}
                    </div>
                )}
            </div>

            <div className="mt-8 bg-white p-6 rounded-lg shadow-md">
                <h2 className="text-xl font-bold text-gray-800 mb-4 pb-3 border-b">{t.recentUploads}</h2>
                <div className="overflow-x-auto">
                    <table className="w-full">
                        <thead>
                            <tr className="bg-gray-100 text-gray-600 uppercase text-sm">
                                <th className="py-3 px-4">{t.fileName}</th>
                                <th className="py-3 px-4">{t.fileType}</th>
                                <th className="py-3 px-4">{t.fileSize}</th>
                                <th className="py-3 px-4">{t.uploadDate}</th>
                                <th className="py-3 px-4">{t.actions}</th>
                            </tr>
                        </thead>
                        <tbody className="text-gray-700">
                            {recentFiles.length > 0 ? recentFiles.map((file) => (
                                <tr key={file.id} className="border-b border-gray-200 hover:bg-gray-50">
                                    <td className="py-3 px-4">
                                        <div className="flex items-center gap-3 max-w-xs">
                                            <Icon path={file.icon.path} className={`w-8 h-8 flex-shrink-0 ${file.icon.color}`} />
                                            <span className="font-semibold truncate" title={file.name}>{file.name}</span>
                                        </div>
                                    </td>
                                    <td className="py-3 px-4">{file.icon.type}</td>
                                    <td className="py-3 px-4">{file.size}</td>
                                    <td className="py-3 px-4">{file.modified}</td>
                                    <td className="py-3 px-4"><div className="flex items-center gap-2">
                                        <button onClick={() => openModal('preview', file)} className="text-gray-500 hover:text-blue-600 p-1"><Icon path="M12 4.5C7 4.5 2.73 7.61 1 12c1.73 4.39 6 7.5 11 7.5s9.27-3.11 11-7.5c-1.73-4.39-6-7.5-11-7.5zM12 17c-2.76 0-5-2.24-5-5s2.24-5 5-5 5 2.24 5 5-2.24 5-5 5zm0-8c-1.66 0-3 1.34-3 3s1.34 3 3 3 3-1.34 3-3-1.34-3-3-3z" className="w-5 h-5" /></button>
                                        <button onClick={() => showToast(`${t.downloading} ${file.name}`)} className="text-gray-500 hover:text-green-600 p-1"><Icon path="M19 9h-4V3H9v6H5l7 7 7-7zM5 18v2h14v-2H5z" className="w-5 h-5" /></button>
                                        <button onClick={() => openModal('delete', file)} className="text-gray-500 hover:text-red-600 p-1"><Icon path="M6 19c0 1.1.9 2 2 2h8c1.1 0 2-.9 2-2V7H6v12zM19 4h-3.5l-1-1h-5l-1 1H5v2h14V4z" className="w-5 h-5" /></button>
                                    </div></td>
                                </tr>
                            )) : (
                                <tr><td colSpan="5" className="text-center py-8 text-gray-500">{t.noFiles}</td></tr>
                            )}
                        </tbody>
