                                className={`flex items-center gap-4 py-3 rounded-md transition-colors duration-200 hover:bg-gray-700 ${isCollapsed ? 'justify-center px-0' : 'px-3'} ${currentPage === item.id ? 'bg-blue-600 text-white' : ''}`}
