export const NewFolderModal = ({ isOpen, onClose, onSubmit, t }: NewFolderModalProps) => {
  const [name, setName] = useState('');

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (name.trim()) {
      onSubmit(name.trim());
