import { useState } from 'react';
import { Icon } from '../ui/Icon';
import { Language } from '../../types';

interface HeaderProps {
  t: any;
  language: Language;
  setLanguage: (lang: Language) => void;
  toggleSidebar: () => void;
}

export const Header = ({ t, language, setLanguage, toggleSidebar }: HeaderProps) => {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const languages = { en: 'English', fr: 'Français', ar: 'العربية' };

  return (
    <header className="flex justify-between items-center mb-8">
      <div className="flex items-center gap-4">
        <button
          onClick={toggleSidebar}
          className="text-gray-600 hover:text-blue-600 p-2 rounded-full hover:bg-gray-200"
        >
          <Icon path="M3 18h18v-2H3v2zm0-5h18v-2H3v2zm0-7v2h18V6H3z" className="w-6 h-6" />
        </button>
        <h1 className="text-3xl font-bold text-gray-800">{t.welcomeAdmin}</h1>
      </div>
      <div className="flex items-center gap-4">
        <div className="relative">
          <button
            onClick={() => setDropdownOpen(!dropdownOpen)}
            className="flex items-center gap-2 bg-white px-4 py-2 rounded-md shadow-sm border"
          >
            <Icon path="M12.87 15.07l-3.75-3.75-3.75 3.75c-.48.48-1.26.48-1.74 0s-.48-1.26 0-1.74l4.59-4.59c.48-.48 1.26-.48 1.74 0l4.59 4.59c.48.48.48 1.26 0 1.74s-1.26.48-1.74 0zM12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm0 18c-4.41 0-8-3.59-8-8s3.59-8 8-8 8 3.59 8 8-3.59 8-8 8z" className="w-5 h-5 text-gray-600" />
            <span>{languages[language]}</span>
          </button>
          {dropdownOpen && (
            <div className="absolute top-full ltr:right-0 rtl:left-0 mt-2 w-32 bg-white rounded-md shadow-lg z-10">
              {Object.entries(languages).map(([code, name]) => (
                <a
                  href="#"
                  key={code}
                  onClick={(e) => {
                    e.preventDefault();
                    setLanguage(code as Language);
                    setDropdownOpen(false);
                  }}
                  className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                >
                  {name}
                </a>
              ))}
            </div>
          )}
        </div>
        <img
          src="https://placehold.co/40x40/E2E8F0/4A5568?text=A"
          alt="Admin"
          className="w-10 h-10 rounded-full"
        />
      </div>
    </header>
  );
};
</content>
