<content>
import { useState, useCallback } from 'react';
import { Icon } from '../ui/Icon';
import { FileItem } from '../../types';
import { FILE_ALLOWED_TYPES } from '../../constants/fileTypes';

interface DashboardPageProps {
  t: any;
  onFileUpload: (files: FileList, parentId: string) => void;
  files: FileItem[];
  openModal: (type: string, item?: FileItem) => void;
  showToast: (message: string) => void;
  onDeleteFile: (fileId: string) => void;
}

export const DashboardPage = ({ t, onFileUpload, files, openModal, showToast }: DashboardPageProps) => {
  const [isDragging, setIsDragging] = useState(false);
  const [uploadError, setUploadError] = useState('');

  const handleFiles = (fileList: FileList) => {
    const validFiles = Array.from(fileList).filter(file => FILE_ALLOWED_TYPES.includes(file.type));
    const invalidFilesCount = fileList.length - validFiles.length;

    if (invalidFilesCount > 0) {
      setUploadError(t.uploadError);
      setTimeout(() => setUploadError(''), 5000);
    } else {
      setUploadError('');
    }

    if (validFiles.length > 0) {
      const validFileList = new DataTransfer();
      validFiles.forEach(file => validFileList.items.add(file));
      onFileUpload(validFileList.files, 'root');
    }
  };

  const handleDragEvents = (e: React.DragEvent, dragging: boolean) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(dragging);
  };

  const handleDrop = useCallback((e: React.DragEvent) => {
    handleDragEvents(e, false);
    handleFiles(e.dataTransfer.files);
  }, [t, onFileUpload]);

  const handleFileSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files) {
      handleFiles(e.target.files);
    }
  };

  const recentFiles = files.filter(f => f.kind === 'file').slice(0, 4);

  return (
    <>
      <div
        className={`bg-white p-6 rounded-lg shadow-md transition-all duration-300 ${
          isDragging ? 'border-blue-500 ring-2 ring-blue-300' : 'border-gray-300'
        }`}
        onDragEnter={(e) => handleDragEvents(e, true)}
        onDragLeave={(e) => handleDragEvents(e, false)}
        onDragOver={(e) => e.preventDefault()}
        onDrop={handleDrop}
      >
        <h2 className="text-xl font-bold text-gray-800 mb-4 pb-3 border-b">{t.uploadNewFile}</h2>
        <div className="border-2 border-dashed border-gray-300 rounded-lg p-8 text-center bg-gray-50">
          <Icon path="M19.35 10.04C18.67 6.59 15.64 4 12 4 9.11 4 6.6 5.64 5.35 8.04 2.34 8.36 0 10.91 0 14c0 3.31 2.69 6 6 6h13c2.76 0 5-2.24 5-5 0-2.64-2.05-4.78-4.65-4.96zM14 13v4h-4v-4H7l5-5 5 5h-3z" className="w-16 h-16 mx-auto text-blue-500" />
          <p className="mt-4 text-gray-600">
            {t.dragDrop} <span className="font-bold text-blue-600">{t.browseFiles}</span>
          </p>
          <input
            type="file"
            id="fileInput"
            multiple
            className="hidden"
            onChange={handleFileSelect}
            accept={FILE_ALLOWED_TYPES.join(',')}
          />
          <button
            onClick={() => document.getElementById('fileInput')?.click()}
            className="mt-6 bg-blue-600 text-white font-semibold py-2 px-6 rounded-lg hover:bg-blue-700 transition-colors duration-300 shadow-sm"
          >
            {t.selectFiles}
          </button>
        </div>
        {uploadError && (
          <div className="mt-4 text-center text-red-600 bg-red-100 p-3 rounded-lg font-semibold">
            {uploadError}
          </div>
        )}
      </div>

      <div className="mt-8 bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-xl font-bold text-gray-800 mb-4 pb-3 border-b">{t.recentUploads}</h2>
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead>
              <tr className="bg-gray-100 text-gray-600 uppercase text-sm">
                <th className="py-3 px-4">{t.fileName}</th>
                <th className="py-3 px-4">{t.fileType}</th>
                <th className="py-3 px-4">{t.fileSize}</th>
                <th className="py-3 px-4">{t.uploadDate}</th>
                <th className="py-3 px-4">{t.actions}</th>
              </tr>
            </thead>
            <tbody className="text-gray-700">
              {recentFiles.length > 0 ? recentFiles.map((file) => (
                <tr key={file.id} className="border-b border-gray-200 hover:bg-gray-50">
                  <td className="py-3 px-4">
                    <div className="flex items-center gap-3 max-w-xs">
                      <Icon path={file.icon.path} className={`w-8 h-8 flex-shrink-0 ${file.icon.color}`} />
                      <span className="font-semibold truncate" title={file.name}>{file.name}</span>
                    </div>
                  </td>
                  <td className="py-3 px-4">{file.icon.type}</td>
                  <td className="py-3 px-4">{file.size}</td>
                  <td className="py-3 px-4">{file.modified}</td>
                  <td className="py-3 px-4">
                    <div className="flex items-center gap-2">
                      <button
                        onClick={() => openModal('preview', file)}
                        className="text-gray-500 hover:text-blue-600 p-1"
                      >
                        <Icon path="M12 4.5C7 4.5 2.73 7.61 1 12c1.73 4.39 6 7.5 11 7.5s9.27-3.11 11-7.5c-1.73-4.39-6-7.5-11-7.5zM12 17c-2.76 0-5-2.24-5-5s2.24-5 5-5 5 2.24 5 5-2.24 5-5 5zm0-8c-1.66 0-3 1.34-3 3s1.34 3 3 3 3-1.34 3-3-1.34-3-3-3z" className="w-5 h-5" />
                      </button>
                      <button
                        onClick={() => showToast(`${t.downloading} ${file.name}`)}
                        className="text-gray-500 hover:text-green-600 p-1"
                      >
                        <Icon path="M19 9h-4V3H9v6H5l7 7 7-7zM5 18v2h14v-2H5z" className="w-5 h-5" />
                      </button>
                      <button
                        onClick={() => openModal('delete', file)}
                        className="text-gray-500 hover:text-red-600 p-1"
                      >
                        <Icon path="M6 19c0 1.1.9 2 2 2h8c1.1 0 2-.9 2-2V7H6v12zM19 4h-3.5l-1-1h-5l-1 1H5v2h14V4z" className="w-5 h-5" />
                      </button>
                    </div>
                  </td>
                </tr>
              )) : (
                <tr>
                  <td colSpan={5} className="text-center py-8 text-gray-500">{t.noFiles}</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </>
  );
};
</content>
