            </div>
        </header>
    );
};

// --- Main App Component ---
export default function App() {
    const [language, setLanguage] = useState('fr');
    const [currentPage, setCurrentPage] = useState('dashboard');
    const [isSidebarCollapsed, setIsSidebarCollapsed] = useState(false);
    const [currentPath, setCurrentPath] = useState([{ id: 'root', name: 'Home' }]);
    const [modalState, setModalState] = useState({ type: null, item: null });
    const [toast, setToast] = useState({ show: false, message: '' });
    const t = translations[language];

    const initialFiles = useMemo(() => [
        { id: 'folder-1', parentId: 'root', kind: 'folder', name: 'Projects', modified: '2025-08-18' },
        { id: 'file-1', parentId: 'root', kind: 'file', name: 'annual-report.docx', size: '5.4 MB', modified: '2025-08-09', mimeType: 'application/vnd.openxmlformats-officedocument.wordprocessingml.document' },
        { id: 'file-2', parentId: 'root', kind: 'file', name: 'quarterly-budget.xlsx', size: '800 KB', modified: '2025-07-30', mimeType: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet' },
        { id: 'file-3', parentId: 'folder-1', kind: 'file', name: 'project-alpha.pdf', size: '2.1 MB', modified: '2025-08-15', mimeType: 'application/pdf' },
    ].map(f => ({ ...f, icon: getFileInfo(f) })), []);

    const [uploadedFiles, setUploadedFiles] = useState(initialFiles);

    const formatFileSize = (bytes, decimals = 2) => {
        if (bytes === 0) return '0 Bytes';
        const k = 1024;
        const dm = decimals < 0 ? 0 : decimals;
        const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB'];
        const i = Math.floor(Math.log(bytes) / Math.log(k));
        return parseFloat((bytes / Math.pow(k, i)).toFixed(dm)) + ' ' + sizes[i];
    };

    const handleFileUpload = (newFiles, parentId) => {
        const newFileObjects = Array.from(newFiles).map(file => {
            const fileObject = { id: `${file.name}-${Date.now()}`, parentId, kind: 'file', name: file.name, size: formatFileSize(file.size), modified: new Date().toISOString().split('T')[0], mimeType: file.type };
            return { ...fileObject, icon: getFileInfo(fileObject) };
        });
        setUploadedFiles(prevFiles => [...newFileObjects, ...prevFiles]);
    };

    const handleRenameFile = (fileId, newName) => {
        setUploadedFiles(files => files.map(file => file.id === fileId ? { ...file, name: newName } : file));
    };

    const handleDeleteFile = (fileId) => {
        const fileToDelete = uploadedFiles.find(f => f.id === fileId);
        if (!fileToDelete) return;

        let idsToDelete = [fileId];
        if (fileToDelete.kind === 'folder') {
            const getChildIds = (folderId) => {
                const children = uploadedFiles.filter(f => f.parentId === folderId);
                children.forEach(child => {
                    idsToDelete.push(child.id);
                    if (child.kind === 'folder') {
                        getChildIds(child.id);
                    }
                });
            };
            getChildIds(fileId);
        }
        setUploadedFiles(prevFiles => prevFiles.filter(file => !idsToDelete.includes(file.id)));
    };

    const handleNewFolder = (name, parentId) => {
        const newFolder = { id: `folder-${Date.now()}`, parentId, kind: 'folder', name, modified: new Date().toISOString().split('T')[0] };
        newFolder.icon = getFileInfo(newFolder);
        setUploadedFiles(prevFiles => [newFolder, ...prevFiles]);
    };

    const openModal = (type, item = null) => {
        setModalState({ type, item });
    };

    const closeModal = () => {
        setModalState({ type: null, item: null });
    };

    const showToast = (message) => {
        setToast({ show: true, message });
    };

    const renderPage = () => {
        switch (currentPage) {
            case 'dashboard': return <DashboardPage t={t} onFileUpload={handleFileUpload} files={uploadedFiles} openModal={openModal} showToast={showToast} onDeleteFile={handleDeleteFile} />;
            case 'manage-files': return <ManageFilesPage t={t} onFileUpload={handleFileUpload} files={uploadedFiles} onNewFolder={handleNewFolder} onRenameFile={handleRenameFile} currentPath={currentPath} setCurrentPath={setCurrentPath} openModal={openModal} showToast={showToast} onDeleteFile={handleDeleteFile} />;
            case 'users': return <UsersPage t={t} />;
            case 'reports': return <ReportsPage t={t} />;
            case 'settings': return <PlaceholderPage title={t.pages.settings} />;
            default: return <DashboardPage t={t} onFileUpload={handleFileUpload} files={uploadedFiles} openModal={openModal} showToast={showToast} onDeleteFile={handleDeleteFile} />;
        }
    };

    return (
        <div dir={t.dir} className="flex h-screen bg-gray-100 font-sans">
            <Sidebar currentPage={currentPage} setCurrentPage={setCurrentPage} t={t} isCollapsed={isSidebarCollapsed} />
            <main className="flex-1 p-8 overflow-y-auto">
                <Header t={t} language={language} setLanguage={setLanguage} toggleSidebar={() => setIsSidebarCollapsed(!isSidebarCollapsed)} />
                {renderPage()}
                <NewFolderModal isOpen={modalState.type === 'newFolder'} onClose={closeModal} onSubmit={(name) => handleNewFolder(name, currentPath[currentPath.length - 1].id)} t={t} />
                <RenameModal isOpen={modalState.type === 'rename'} onClose={closeModal} onSubmit={(newName) => handleRenameFile(modalState.item.id, newName)} t={t} currentName={modalState.item?.name || ''} />
                <ConfirmDeleteModal isOpen={modalState.type === 'delete'} onClose={closeModal} onConfirm={() => { handleDeleteFile(modalState.item.id); closeModal(); }} t={t} item={modalState.item} />
                <FilePreviewModal isOpen={modalState.type === 'preview'} onClose={closeModal} t={t} item={modalState.item} />
                <Toast message={toast.message} show={toast.show} onHide={() => setToast({ show: false, message: '' })} />
            </main>
        </div>
    );
}

